"use client"

import { useState, useEffect, useRef } from "react"
import {
  View,
  Text,
  StyleSheet,
  SafeAreaView,
  TouchableOpacity,
  ScrollView,
  TextInput,
  Image,
  StatusBar,
  Dimensions,
  Animated,
  FlatList,
  RefreshControl,
} from "react-native"
import { ChevronLeft, Search, Grid3X3, List, Home, Library, Play, MoreVertical, Clock } from "lucide-react-native"
import { useNavigation } from '@react-navigation/native';

const { width, height } = Dimensions.get("window")
const CARD_WIDTH = (width - 60) / 2

const PlaylistsScreen = () => {
  const navigation = useNavigation();
  const [searchText, setSearchText] = useState("")
  const [activeTab, setActiveTab] = useState("library")
  const [viewMode, setViewMode] = useState("grid") // 'grid' or 'list'
  const [selectedFilter, setSelectedFilter] = useState("Recents")
  const [refreshing, setRefreshing] = useState(false)
  const [selectedPlaylist, setSelectedPlaylist] = useState(null)

  // Animation values
  const fadeAnim = useRef(new Animated.Value(0)).current
  const slideAnim = useRef(new Animated.Value(50)).current
  const scaleAnim = useRef(new Animated.Value(0.9)).current

  const playlists = [
    {
      id: "1",
      title: "Certified Lover Boy",
      subtitle: "Playlist • Myself",
      image: "data:image/jpeg;base64,",
      songCount: 25,
      duration: "1h 32m",
      lastPlayed: "2 days ago",
      isOwned: true,
      color: "#4ECDC4",
    },
    {
      id: "2",
      title: "IRON BOY",
      subtitle: "Playlist",
      image: "data:image/jpeg;base64,",
      songCount: 18,
      duration: "58m",
      lastPlayed: "1 week ago",
      isOwned: false,
      color: "#3B82F6",
    },
    {
      id: "3",
      title: "The Kadosh",
      subtitle: "Playlist • Myself",
      image: "data:image/jpeg;base64,",
      songCount: 42,
      duration: "2h 45m",
      lastPlayed: "3 days ago",
      isOwned: true,
      color: "#EF4444",
    },
    {
      id: "4",
      title: "Pink Friday 2",
      subtitle: "2023 • Album",
      image: "data:image/jpeg;base64,",
      songCount: 22,
      duration: "1h 18m",
      lastPlayed: "5 days ago",
      isOwned: false,
      color: "#EC4899",
    },
    {
      id: "5",
      title: "5th Dimension",
      subtitle: "Playlist • Myself",
      image: "data:image/jpeg;base64,",
      songCount: 31,
      duration: "2h 12m",
      lastPlayed: "1 day ago",
      isOwned: true,
      color: "#F59E0B",
    },
    {
      id: "6",
      title: "11:11",
      subtitle: "Playlist • Myself",
      image: "data:image/jpeg;base64,",
      songCount: 11,
      duration: "44m",
      lastPlayed: "6 days ago",
      isOwned: true,
      color: "#8B5CF6",
    },
  ]

  const filters = ["Recents", "Recently Added", "Alphabetical", "Creator"]

  useEffect(() => {
    // Entrance animation
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 800,
        useNativeDriver: true,
      }),
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 600,
        useNativeDriver: true,
      }),
      Animated.timing(scaleAnim, {
        toValue: 1,
        duration: 700,
        useNativeDriver: true,
      }),
    ]).start()
  }, [])

  const handleBackPress = () => {
    if (navigation) {
      navigation.goBack()
    }
  }

  const handlePlaylistPress = (playlist) => {
    setSelectedPlaylist(playlist.id)
    // Add haptic feedback here if available
    console.log(`Opening playlist: ${playlist.title}`)
  }

  const handlePlayPress = (playlist) => {
    console.log(`Playing playlist: ${playlist.title}`)
  }

  const handleMorePress = (playlist) => {
    console.log(`More options for: ${playlist.title}`)
  }

  const onRefresh = () => {
    setRefreshing(true)
    // Simulate refresh
    setTimeout(() => {
      setRefreshing(false)
    }, 1500)
  }

  const filteredPlaylists = playlists.filter(
    (playlist) =>
      playlist.title.toLowerCase().includes(searchText.toLowerCase()) ||
      playlist.subtitle.toLowerCase().includes(searchText.toLowerCase()),
  )

  const renderGridItem = ({ item, index }) => {
    const animatedStyle = {
      opacity: fadeAnim,
      transform: [
        {
          translateY: slideAnim.interpolate({
            inputRange: [0, 50],
            outputRange: [0, 50],
          }),
        },
        { scale: scaleAnim },
      ],
    }

    return (
      <Animated.View style={[animatedStyle, { marginLeft: index % 2 === 0 ? 0 : 10 }]}>
        <TouchableOpacity
          style={[styles.playlistCard, { backgroundColor: item.color + "15" }]}
          onPress={() => handlePlaylistPress(item)}
          activeOpacity={0.8}
        >
          <View style={styles.cardImageContainer}>
            <Image source={{ uri: item.image }} style={styles.cardImage} />
            <View style={styles.playOverlay}>
              <TouchableOpacity style={styles.playButton} onPress={() => handlePlayPress(item)}>
                <Play size={20} color="#FFFFFF" fill="#FFFFFF" />
              </TouchableOpacity>
            </View>
          </View>

          <View style={styles.cardContent}>
            <Text style={styles.cardTitle} numberOfLines={1}>
              {item.title}
            </Text>
            <Text style={styles.cardSubtitle} numberOfLines={1}>
              {item.subtitle}
            </Text>

            <View style={styles.cardMeta}>
              <View style={styles.metaItem}>
                <Text style={styles.metaText}>{item.songCount} songs</Text>
              </View>
              <View style={styles.metaItem}>
                <Clock size={12} color="#666666" />
                <Text style={styles.metaText}>{item.duration}</Text>
              </View>
            </View>
          </View>

          <TouchableOpacity style={styles.cardMoreButton} onPress={() => handleMorePress(item)}>
            <MoreVertical size={16} color="#666666" />
          </TouchableOpacity>
        </TouchableOpacity>
      </Animated.View>
    )
  }

  const renderListItem = ({ item }) => (
    <TouchableOpacity style={styles.listItem} onPress={() => handlePlaylistPress(item)} activeOpacity={0.7}>
      <Image source={{ uri: item.image }} style={styles.listImage} />

      <View style={styles.listContent}>
        <Text style={styles.listTitle} numberOfLines={1}>
          {item.title}
        </Text>
        <Text style={styles.listSubtitle} numberOfLines={1}>
          {item.subtitle}
        </Text>
        <View style={styles.listMeta}>
          <Text style={styles.listMetaText}>
            {item.songCount} songs • {item.duration}
          </Text>
        </View>
      </View>

      <View style={styles.listActions}>
        <TouchableOpacity style={styles.listPlayButton} onPress={() => handlePlayPress(item)}>
          <Play size={18} color="#FFFFFF" fill="#FFFFFF" />
        </TouchableOpacity>
        <TouchableOpacity style={styles.listMoreButton} onPress={() => handleMorePress(item)}>
          <MoreVertical size={18} color="#666666" />
        </TouchableOpacity>
      </View>
    </TouchableOpacity>
  )

  const renderFilterButton = (filter) => (
    <TouchableOpacity
      key={filter}
      style={[styles.filterButton, selectedFilter === filter && styles.activeFilterButton]}
      onPress={() => setSelectedFilter(filter)}
    >
      <Text style={[styles.filterText, selectedFilter === filter && styles.activeFilterText]}>{filter}</Text>
    </TouchableOpacity>
  )

  return (
    <SafeAreaView style={styles.container}>
      <StatusBar barStyle="light-content" backgroundColor="#000000" />

      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity style={styles.backButton} onPress={handleBackPress}>
          <ChevronLeft size={24} color="#FFFFFF" />
        </TouchableOpacity>
        <View style={styles.headerContent}>
          <Text style={styles.headerTitle}>Playlists</Text>
        </View>
        <TouchableOpacity
          style={styles.viewModeButton}
          onPress={() => setViewMode(viewMode === "grid" ? "list" : "grid")}
        >
          {viewMode === "grid" ? <List size={20} color="#FFFFFF" /> : <Grid3X3 size={20} color="#FFFFFF" />}
        </TouchableOpacity>
      </View>

      {/* Playlist Count */}
      <View style={styles.countContainer}>
        <Text style={styles.countText}>{filteredPlaylists.length} playlists</Text>
      </View>

      {/* Search Bar */}
      <View style={styles.searchContainer}>
        <View style={styles.searchBar}>
          <Search size={18} color="#666666" style={styles.searchIcon} />
          <TextInput
            style={styles.searchInput}
            placeholder="Search"
            placeholderTextColor="#666666"
            value={searchText}
            onChangeText={setSearchText}
          />
        </View>
      </View>

      {/* Filter Buttons */}
      <ScrollView
        horizontal
        showsHorizontalScrollIndicator={false}
        style={styles.filtersContainer}
        contentContainerStyle={styles.filtersContent}
      >
        {filters.map(renderFilterButton)}
      </ScrollView>

      {/* Playlists Content */}
      <FlatList
        data={filteredPlaylists}
        renderItem={viewMode === "grid" ? renderGridItem : renderListItem}
        keyExtractor={(item) => item.id}
        numColumns={viewMode === "grid" ? 2 : 1}
        key={viewMode} // Force re-render when view mode changes
        style={styles.playlistsList}
        contentContainerStyle={styles.playlistsContent}
        showsVerticalScrollIndicator={false}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} tintColor="#FFFFFF" colors={["#A855F7"]} />
        }
      />

      {/* Bottom Navigation */}
      <View style={styles.bottomNav}>
        <TouchableOpacity style={styles.navItem} onPress={() => setActiveTab("home")}>
          <Home size={24} color="#666666" />
          <Text style={styles.navText}>Home</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.navItem} onPress={() => setActiveTab("search")}>
          <Search size={24} color="#666666" />
          <Text style={styles.navText}>Search</Text>
        </TouchableOpacity>

        <TouchableOpacity style={[styles.navItem, styles.activeNavItem]} onPress={() => setActiveTab("library")}>
          <Library size={24} color="#A855F7" />
          <Text style={[styles.navText, styles.activeNavText]}>Your Library</Text>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  )
}

